mapper.select:
SELECT d.date,
a.balance,
a.deposit_tx_count + a.withdraw_tx_count AS tx_count,
a.deposit_address_count AS deposit_addr,
a.withdraw_address_count AS withdraw_addr
FROM tbl_address_daily_aggregation a
INNER JOIN tbl_date d ON d.id = a.date_id
WHERE a.address = #{address}
AND a.token_contract_hash = #{tokenContractHash}
AND a.date_id > 0

    AND d.date >= #{from}


    AND d.date <= #{to}

ORDER BY a.date_id

SELECT d.date, a.contract_count, a.deposit_tx_count AS deposit_count, a.withdraw_tx_count AS withdraw_count, a.deposit_tx_count + a.withdraw_tx_count AS tx_count, a.deposit_address_count AS deposit_addr, a.withdraw_address_count AS withdraw_addr, a.tx_address_count AS tx_addr FROM tbl_address_daily_aggregation a INNER JOIN tbl_date d ON d.id = a.date_id WHERE a.address = #{address} AND a.token_contract_hash = #{tokenContractHash} AND a.date_id > 0
AND d.date >= #{from}
AND d.date <= #{to}
ORDER BY a.date_id

SELECT deposit_tx_count + withdraw_tx_count FROM tbl_address_daily_aggregation aggr
LEFT OUTER JOIN tbl_contract c ON c.contract_hash = aggr.token_contract_hash
WHERE aggr.address = #{address}
AND c.name = #{asset_name}
AND aggr.token_contract_hash = '$$ALL$$'
AND aggr.date_id <= 0 AND date_id <> -(~0 >> 33) - 1 ORDER BY aggr.update_time DESC LIMIT 1
