mapper.select:
SELECT c.contract_hash,
       if(oep4.contract_hash IS NULL, FALSE, TRUE)  AS oep4,
       if(oep5.contract_hash IS NULL, FALSE, TRUE)  AS oep5,
       if(oep8.contract_hash IS NULL, FALSE, TRUE)  AS oep8,
       if(erc20.contract_hash IS NULL, FALSE, TRUE) AS erc20
FROM tbl_contract c
         LEFT OUTER JOIN tbl_oep4 oep4 ON oep4.contract_hash = c.contract_hash
         LEFT OUTER JOIN tbl_oep5 oep5 ON oep5.contract_hash = c.contract_hash
         LEFT OUTER JOIN tbl_oep8 oep8 ON oep8.contract_hash = c.contract_hash
         LEFT OUTER JOIN tbl_erc20 erc20 ON erc20.contract_hash = c.contract_hash
WHERE c.contract_hash = #{contractHash}
LIMIT 1

SELECT COUNT(*)
FROM tbl_contract
WHERE contract_hash = #{contractHash}
