mapper.select:
SELECT a.contract_hash AS contractHash, a.name AS NAME, a.creator AS creator, a.description AS description, a.logo AS logo, a.create_time AS createTime, a.update_time AS updateTime, a.contact_info AS contactInfo, a.address_count AS addressCount, a.tx_count AS txCount, b.total_supply AS totalSupply, b.decimals AS decimals, b.symbol AS symbol, b.vm_category AS vmCategory FROM tbl_contract a, tbl_erc20 b WHERE a.contract_hash = b.contract_hash AND a.audit_flag = 1 AND b.audit_flag = 1
, a.${sort}
, a.${sort} desc
