mapper.select:
SELECT *
FROM
(
    SELECT 
    FROM tbl_tx_detail
    WHERE from_address = #{address}

        and asset_name = #{assetName}

    AND event_type IN (2, 3)
      UNION ALL
    SELECT 
    FROM tbl_tx_detail
    WHERE to_address = #{address}

        and asset_name = #{assetName}

    AND event_type IN (2, 3)
) a
ORDER BY a.blockHeight DESC, a.txHash, a.txIndex
LIMIT #{startIndex}, #{pageSize}

SELECT *
FROM
(
    SELECT 
    FROM tbl_tx_detail
    WHERE from_address = #{address}

        and asset_name = #{assetName}

    AND event_type IN (2, 3)
      UNION ALL
    SELECT 
    FROM tbl_tx_detail
    WHERE to_address = #{address}

        and asset_name = #{assetName}

    AND event_type IN (2, 3)
) a
where a.txTime >= #{startTime}
and  a.txTime <= #{endTime} 
ORDER BY a.blockHeight DESC, a.txHash, a.txIndex

SELECT *
FROM
(
    SELECT 
    FROM tbl_tx_detail
    WHERE from_address = #{address}

        and asset_name = #{assetName}

    AND event_type = 3
    UNION ALL
    SELECT 
    FROM tbl_tx_detail
    WHERE to_address = #{address}

        and asset_name = #{assetName}

    AND event_type = 3
) a
where  a.txTime < #{endTime} 
ORDER BY a.txTime DESC
limit #{pageSize}

SELECT *
FROM
(
    SELECT 
    FROM tbl_tx_detail
    WHERE from_address = #{address}
    and asset_name like #{assetName}
    AND event_type = 3
    UNION ALL
    SELECT 
    FROM tbl_tx_detail
    WHERE to_address = #{address}
    and asset_name like #{assetName}
    AND event_type = 3
) a
where a.txTime >= #{startTime}
and  a.txTime <= #{endTime} 
ORDER BY a.blockHeight DESC, a.txHash, a.txIndex

SELECT *
FROM
(
    SELECT 
    FROM tbl_tx_detail
    WHERE from_address = #{address}
    and asset_name like #{assetName}
    AND event_type = 3
    UNION ALL
    SELECT 
    FROM tbl_tx_detail
    WHERE to_address = #{address}
    and asset_name like #{assetName}
    AND event_type = 3
) a
where  a.txTime < #{endTime} 
ORDER BY a.txTime DESC
limit #{pageSize}

SELECT count(1)
FROM tbl_tx_detail
WHERE from_address = #{address}
OR to_address = #{address}
