mapper.select:
select 
from tbl_tx_detail
where from_address = #{address}

    and asset_name = #{assetName}

and event_type in (2,3)
order by block_height DESC
limit #{startIndex}, #{pageSize}

select 
from tbl_tx_detail
where to_address = #{address}

    and asset_name = #{assetName}

and event_type in (2,3)
order by block_height DESC
limit #{startIndex}, #{pageSize}

SELECT count(1)
FROM tbl_tx_detail
WHERE from_address = #{address}
OR to_address = #{address}
