mapper.select:
SELECT COUNT(DISTINCT tx_hash)
FROM tbl_tx_detail_index
WHERE address = #{address}

    AND asset_name = #{assetName}

SELECT d.tx_hash as txHash, d.tx_type as txType, d.tx_time as txTime, d.block_height as blockHeight, d.confirm_flag as confirmFlag, d.block_index as blockIndex, d.fee as fee, d.asset_name as assetName, d.amount as amount, d.to_address as toAddress, d.from_address as fromAddress, d.tx_index as txIndex, d.contract_hash as contractHash, c.type AS assetType FROM tbl_tx_detail d
LEFT JOIN tbl_contract c ON d.contract_hash = c.contract_hash WHERE (d.from_address = #{address} OR d.to_address = #{address})
AND d.asset_name = #{assetName}
AND d.event_type IN (2, 3) ORDER BY block_height DESC, block_index, tx_hash, tx_index LIMIT #{startIndex}, #{pageSize}

SELECT

FROM tbl_tx_detail d
INNER JOIN
(
SELECT tx_hash AS `hash`, tx_index AS `index`
FROM tbl_tx_detail_index
WHERE address = #{address}
AND tx_time >= #{startTime}
AND tx_time <= #{endTime}

    AND asset_name = #{assetName}

ORDER BY desc_block_height, tx_hash, tx_index
) idx ON idx.hash = d.tx_hash AND idx.`index` = d.tx_index
ORDER BY block_height DESC, block_index

SELECT *
FROM
(
SELECT

FROM tbl_tx_detail
WHERE from_address = #{address}

    and asset_name = #{assetName}

AND event_type = 3
UNION ALL
SELECT

FROM tbl_tx_detail
WHERE to_address = #{address}

    and asset_name = #{assetName}

AND event_type = 3
) a
where a.txTime >= #{startTime}
and  a.txTime <= #{endTime} 
ORDER BY a.blockHeight DESC, a.blockIndex, a.txHash, a.txIndex

SELECT

FROM tbl_tx_detail
WHERE from_address = #{address}

    and asset_name = #{assetName}

AND event_type = 3
AND tx_time >= #{startTime}
and  tx_time <= #{endTime} 
ORDER BY block_height DESC, block_index, tx_hash, tx_index

