mapper.select:
select Height, Hash, PrevBlock, NextBlock, TxnsRoot, BlockTime, ConsensusData, BookKeeper, TxnNum, BlockSize
from tbl_ont_block
where height = #{height}

select Height, Hash, PrevBlock, NextBlock, TxnsRoot, BlockTime, ConsensusData, BookKeeper, TxnNum, BlockSize
from tbl_ont_block
where hash = #{hash}

select count(*) as nums
from tbl_ont_block
where blocktime >= #{StartTime}
and  blocktime < #{EndTime}
