mapper.update:
update tbl_ont_daily
set txncount = #{txncount,jdbcType=INTEGER},
  ontidcount = #{ontidcount,jdbcType=INTEGER},
  blockcount = #{blockcount,jdbcType=INTEGER}
where time = #{time,jdbcType=DATE}

mapper.select:
select TxnCount, BlockCount, OntIdCount, DATE_FORMAT(time,'%Y-%m-%d') as Time
from tbl_ont_daily
where UNIX_TIMESTAMP(time) >= #{StartTime}
and  UNIX_TIMESTAMP(time) < #{EndTime} 
order by time desc
