mapper.select:
select distinct TxnHash, Height, TxnType, TxnTime, ConfirmFlag, Fee, TokenId
from tbl_ont_oep8txn_detail
where contracthash = #{contractHash}

  and tokenId = #{tokenId,jdbcType=VARCHAR}

order by height DESC
limit #{Start}, #{PageSize}

select count(DISTINCT(txnhash))
from tbl_ont_oep8txn_detail
where contracthash = #{contractHash}

  and tokenid = #{tokenId,jdbcType=VARCHAR}
