mapper.select:
SELECT SUM(amount)
from tbl_ont_txn_detail_tmp
where contracthash = #{address} and confirmflag = 1 and assetname = #{assetname}

select count(DISTINCT(txnhash))
from tbl_ont_txn_detail_tmp
where contracthash = #{param1}

SELECT DISTINCT(address) FROM (
    select DISTINCT (fromaddress) as address
    from tbl_ont_txn_detail_tmp
    where contracthash = #{param1}
UNION
    select DISTINCT(toaddress) as address
    from tbl_ont_txn_detail_tmp
    where contracthash = #{param1}
) a ORDER BY address

SELECT DISTINCT(address) FROM (
    select DISTINCT (fromaddress) as address
    from tbl_ont_txn_detail_tmp
    where toaddress = #{param1}
UNION
    select DISTINCT(toaddress) as address
    from tbl_ont_txn_detail_tmp
    where fromaddress = #{param1}
) a ORDER BY address
