mapper.select:
select 

from tbl_ont_txn_detail
where txnhash = #{txnhash,jdbcType=VARCHAR}
  and txnindex = #{txnindex,jdbcType=INTEGER}

mapper.delete:
delete from tbl_ont_txn_detail
where txnhash = #{txnhash,jdbcType=VARCHAR}
  and txnindex = #{txnindex,jdbcType=INTEGER}

mapper.select:
select t2.* from (
select TxnHash, Height, TxnType, TxnTime, ConfirmFlag, BlockIndex, Fee, AssetName, Amount, ToAddress, FromAddress, Txnindex
from tbl_ont_txn_detail
where toaddress = #{Address}

  and assetname = #{AssetName}

and eventtype in (1,3)
union all
select TxnHash, Height, TxnType, TxnTime, ConfirmFlag, BlockIndex, Fee, AssetName, Amount, ToAddress, FromAddress, Txnindex
from tbl_ont_txn_detail
where fromaddress = #{Address}

  and assetname = #{AssetName}

and eventtype in (1,3)
) as t2
order by t2.height DESC, t2.txnhash, t2.Txnindex
limit #{Start}, #{PageSize}

select t2.* from (
select TxnHash, Height, TxnType, TxnTime, ConfirmFlag, BlockIndex, Fee, AssetName, Amount, ToAddress, FromAddress
from tbl_ont_txn_detail
where toaddress = #{Address}

  and assetname = #{AssetName}

and  eventtype = 3
union all
select TxnHash, Height, TxnType, TxnTime, ConfirmFlag, BlockIndex, Fee, AssetName, Amount, ToAddress, FromAddress
from tbl_ont_txn_detail
where fromaddress = #{Address}

  and assetname = #{AssetName}

and  eventtype = 3
) as t2
where  t2.TxnTime < #{EndTime} 
order by t2.TxnTime desc
limit #{PageSize}

select t2.* from (
select TxnHash, Height, TxnType, TxnTime, ConfirmFlag, BlockIndex, Fee, AssetName, Amount, ToAddress, FromAddress
from tbl_ont_txn_detail
where toaddress = #{Address}

  and assetname = #{AssetName}

and  eventtype = 3
union all
select TxnHash, Height, TxnType, TxnTime, ConfirmFlag, BlockIndex, Fee, AssetName, Amount, ToAddress, FromAddress
from tbl_ont_txn_detail
where fromaddress = #{Address}

  and assetname = #{AssetName}

and  eventtype = 3
) as t2
where t2.TxnTime >= #{BeginTime}

  and   t2.TxnTime < #{EndTime} 

order by t2.TxnTime desc

select count(distinct(t2.txnhash)) from (
select txnhash
from tbl_ont_txn_detail
where toaddress = #{Address}

  and assetname = #{AssetName}

and eventtype in (1,3)
union all
select txnhash
from tbl_ont_txn_detail
where fromaddress = #{Address}

  and assetname = #{AssetName}

and eventtype in (1,3)
) as t2
