mapper.insert:
INSERT INTO tbl_tx_detail_index (address, desc_block_height, tx_hash, tx_index, called_contract_hash, tx_time, asset_name, tx_direction) SELECT from_address, (~0 >> 33) - block_height, tx_hash, tx_index, called_contract_hash, tx_time, asset_name, 0 FROM tbl_tx_detail WHERE block_height BETWEEN #{beginHeight} AND #{endHeight} AND from_address <> '' AND event_type IN (2, 3)
AND called_contract_hash = #{contractHash}
ORDER BY block_height, tx_time ON DUPLICATE KEY UPDATE tx_direction = 1;

INSERT INTO tbl_tx_detail_index (address, desc_block_height, tx_hash, tx_index, called_contract_hash, tx_time, asset_name, tx_direction) SELECT to_address, (~0 >> 33) - block_height, tx_hash, tx_index, called_contract_hash, tx_time, asset_name, 1 FROM tbl_tx_detail WHERE block_height BETWEEN #{beginHeight} AND #{endHeight} AND to_address <> '' AND event_type IN (2, 3)
AND called_contract_hash = #{contractHash}
ORDER BY block_height, tx_time ON DUPLICATE KEY UPDATE tx_direction = 2;
